function removeAccents(s) {
  const map = {
    'á':'a','à':'a','ã':'a','â':'a','ä':'a',
    'Á':'a','À':'a','Ã':'a','Â':'a','Ä':'a',
    'é':'e','è':'e','ê':'e','ë':'e',
    'É':'e','È':'e','Ê':'e','Ë':'e',
    'í':'i','ì':'i','î':'i','ï':'i',
    'Í':'i','Ì':'i','Î':'i','Ï':'i',
    'ó':'o','ò':'o','õ':'o','ô':'o','ö':'o',
    'Ó':'o','Ò':'o','Õ':'o','Ô':'o','Ö':'o',
    'ú':'u','ù':'u','û':'u','ü':'u',
    'Ú':'u','Ù':'u','Û':'u','Ü':'u',
    'ç':'c','Ç':'c','ñ':'n','Ñ':'n'
  }
  return s.replace(/[áàãâäÁÀÃÂÄéèêëÉÈÊËíìîïÍÌÎÏóòõôöÓÒÕÔÖúùûüÚÙÛÜçÇñÑ]/g, function(ch) {
    return map[ch] || ch
  })
}

function stripEmojisAndSymbols(s) {
  s = s.replace(/[\uD800-\uDBFF][\uDC00-\uDFFF]/g, "")
  s = s.replace(/[^a-z0-9\s]/g, "")
  return s
}

function classify(normalized) {
  const areas = {
    "Direito": ["direito","leis","advocacia","juridico"],
    "Exatas": ["exatas","matematica","engenharia","fisica","quimica"],
    "Administração": ["administracao","gestao","empresas","financeiro","adm","adiministracao"],
    "Educação": ["educacao","pedagogia","ensino","professor","aprendizagem"],
    "Saúde": ["saude","medicina","enfermagem","nutricao","psicologia"],
    "TI": ["ti","tecnologia","informatica","programacao","sistemas","tecnologia da informacao"]
  }

  for (let area in areas) {
    if (areas.hasOwnProperty(area)) {
      const keywords = areas[area]
      for (let i = 0; i < keywords.length; i++) {
        if (normalized.indexOf(keywords[i]) !== -1) {
          return area
        }
      }
    }
  }
  return undefined
}

function run(area) {
  try {
    var lowered = String(area).toLowerCase()
    var noAccents = removeAccents(lowered)
    var cleaned = stripEmojisAndSymbols(noAccents).replace(/\s+/g, " ").replace(/^\s+|\s+$/g, "")
    var result = classify(cleaned)
    return result
  } catch (e) {
    return undefined
  }
}

