function run(coursesJson, isPostGraduateJs, interestAreaJs) {

    const safeParseJson = (s) => {
        if (typeof s !== "string") return s
        try {
            return JSON.parse(s)
        } catch (e) {
            return null
        }
    }

    const toNumber = (v) => {
        if (v === null || v === undefined) return 0
        if (typeof v === "number") return v
        let s = String(v).trim()

        if (s.includes(',')) {
             s = s.replace(/\./g, "").replace(/,/g, ".")
        }

        const n = parseFloat(s)
        return isNaN(n) ? 0 : n
    }

    try {
        const catalog = safeParseJson(coursesJson)

        if (!Array.isArray(catalog) || catalog.length === 0) {
            return JSON.stringify({ ok: false, error: "Catálogo inválido ou vazio" })
        }

        const isPost = isPostGraduateJs == "true" ? true : false 
        const area = (interestAreaJs ?? "").toString().trim().toLowerCase()
        const nivelWanted = isPost ? "pos" : "graduacao"
        const areaIsSet = area.length > 0

        const filtered = catalog
            .map(it => {
                const itNivel = (it.nivel ?? "").toString().trim().toLowerCase()
                const itArea = (it.area ?? "").toString().trim().toLowerCase()

                const preco = toNumber(
                    it.preco_base ?? it.precoBase ?? it.preco ?? it.price ?? 0
                )

                return {
                    id: it.id ?? "",
                    nome: it.nome ?? it.curso ?? "",
                    nivel: itNivel,
                    area: itArea,
                    preco_base: preco, 
                    duracao: it.duracao ?? "",
                    modalidade: it.modalidade ?? "",
                    principal_area: it.principal_area ?? "",
                    cargos_possiveis: it.cargos_possiveis.replace(".", ",") ?? "",
                }
            })
            .filter(cursoObj => {
                if (cursoObj.nivel !== nivelWanted) return false

                if (areaIsSet && cursoObj.area.indexOf(area) === -1) return false

                return true
            })
            .sort((a, b) => {
                if (a.preco_base !== b.preco_base) {
                    return a.preco_base - b.preco_base
                }

                const an = (a.nome ?? "").toLowerCase()
                const bn = (b.nome ?? "").toLowerCase()
                if (an < bn) return -1
                if (an > bn) return 1
                return 0
            })

        return JSON.stringify({
            ok: true,
            nivel: nivelWanted,
            area: area,
            total: filtered.length,
            filtered: filtered
        })

    } catch (e) {
        return JSON.stringify({ ok: false, error: `Erro inesperado: ${String(e)}` })
    }
}

function run(isWorkingJs, isReferrealJs, isRecorrenceJs, isUrgencyJs) {
  function toBool(v) {
    if ( v === "true") return true
    return false
  }

  const isWorking = toBool(isWorkingJs)
  const isReferreal = toBool(isReferrealJs)
  const isRecorrence = toBool(isRecorrenceJs)
  const isUrgency = toBool(isUrgencyJs)

  let discount = 0

  if (isReferreal) discount += 5
  if (isRecorrence) discount += 5
  if (isWorking) discount += 10
  if (isUrgency) discount += 7

  if (discount > 20) discount = 20

  return discount
}

function run(coursesJs, totalDiscountJs) {
    const safeParseJson = (s) => {
        if (typeof s !== "string") return s
        try {
            return JSON.parse(s)
        } catch (e) {
            return null
        }
    }

    const toNumber = (v) => {
        if (typeof v === "number") return v
        if (v === null || v === undefined) return 0
        let s = String(v).trim()
        s = s.replace(/\./g, "").replace(/,/g, ".")
        const n = parseFloat(s)
        return isNaN(n) ? 0 : n
    }

    const formatBRL = (n) => {
        const value = toNumber(n)
        return new Intl.NumberFormat('pt-BR', {
            style: 'currency',
            currency: 'BRL',
            minimumFractionDigits: 2,
            maximumFractionDigits: 2
        }).format(value)
    }

    const round2 = (n) => Math.round(n * 100) / 100

    try {
        const obj = safeParseJson(coursesJs)
        let list = []

        if (Array.isArray(obj.filtered)) {
            list = obj.filtered
        } else if (Array.isArray(obj)) {
            list = obj
        } else {
            return undefined
        }

        let disc = toNumber(totalDiscountJs)
        disc = Math.max(0, Math.min(100, disc))

        const take = list
            .slice(0, 3)
            .map(course => {
                const base = toNumber(course.preco_base ?? course.precoBase ?? 0)
                const finalPrice = round2(base * (1 - disc / 100))

                return {
                    id: course.id ?? "",
                    nome: course.nome ?? "",
                    preco_base: base,
                    preco_final: finalPrice,
                    preco_base_str: formatBRL(base),
                    preco_final_str: formatBRL(finalPrice),
                    duracao: course.duracao ?? "",
                    modalidade: course.modalidade ?? ""
                }
            })

        if (take.length === 0) {
            return "erro"
        }

        const lines = take.map((course, index) => {
            const idx = index + 1
            return `${idx}:  <b>${course.nome}</b> — ${disc > 0? "de" : "por"} ${course.preco_base_str}/mês ${disc > 0 ? `por <b>${course.preco_final_str}/mês</b> \uD83D\uDCB8` : ""}`
        })

        const footer = disc > 0 ? `\n\nTodos com o teu desconto especial de <b>${disc}% OFF aplicado!</b> 🎉` : ""
        const message = lines.join("\n") + footer

        return message

    } catch (e) {
        return "erro"
    }
}

