function run(responseApiGetNationalHolidays) {

    const safeParseJson = (s) => {
        if (typeof s !== "string") return s
        try {
            return JSON.parse(s)
        } catch (e) {
            return null
        }
    }

    const getTodayDateString = () => {
        const d = new Date("2025-11-15")
        const year = d.getFullYear()
        const month = String(d.getMonth() + 1).padStart(2, '0')
        const day = String(d.getDate()).padStart(2, '0')
        return `${year}-${month}-${day}`
    }

    const getDateStringAfterDays = (days) => {
        const d = new Date("2025-11-15")
        d.setDate(d.getDate() + days)
        const year = d.getFullYear()
        const month = String(d.getMonth() + 1).padStart(2, '0')
        const day = String(d.getDate()).padStart(2, '0')
        return `${year}-${month}-${day}`
    }

    try {
        const obj = safeParseJson(responseApiGetNationalHolidays)
        let holidayList = []

        if (Array.isArray(obj)) {
            holidayList = obj
        } else {
            return {
                isHolidayClose: false,
                holidayName: null,
                error: "Lista de feriados inválida ou vazia."
            }
        }

        const todayDateStr = getTodayDateString()
        const limitDateStr = getDateStringAfterDays(2)

        const today = new Date(todayDateStr)
        const limitDate = new Date(limitDateStr)

        const nextHoliday = holidayList.find(holiday => {
            const holidayDate = new Date(holiday.date)

            return holidayDate >= today && holidayDate <= limitDate
        })

        if (nextHoliday) {
            return {
                isHolidayClose: true,
                holidayName: nextHoliday.name,
                holidayDate: nextHoliday.date
            }
        } else {
            return {
                isHolidayClose: false,
                holidayName: null,
            }
        }

    } catch (e) {
        return { 
            isHolidayClose: false, 
            holidayName: null, 
            error: `Erro inesperado: ${String(e)}` 
        }
    }
}

